import { useEffect, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import axios from "axios";

import InserirRegistro from "./InserirRegistro";

function TelaRegistros() {

    const tokenLS = localStorage.getItem("token");
    const usuarioLS = localStorage.getItem("usuario");

    const [registros, setRegistros] = useState([]);
    const [saldo, setSaldo] = useState(0);

    let soma = 0;

    const servidor = "http://localhost:5000/registros";

    const config = {
        headers: {
            "Authorization": `Bearer ${tokenLS}`
        }
    }

    useEffect(() => {
        const promise = axios.get(servidor, config);
        promise.then(response => {
            const { data } = response;
            setRegistros(data);
            calcularTotal(data);
        });
        promise.catch(err => console.log(err.response.statusText));
    }, []);

    function calcularTotal(parametros) {
        parametros.forEach(parametro => {
            let { valor, status } = parametro;
            valor = parseFloat(valor);
            if (status === "entrada") soma += valor;
            else soma -= valor;
        })
        setSaldo(soma);
    }

    function fazerLogout () {
        const mensagem = "Deseja sair da aplicação?";
        const resultado = window.confirm(mensagem)
        if (resultado) navigate("/")
    }

    const navigate = useNavigate();
    return (
        <>
            <Header>
                <Title><span>Olá,{usuarioLS}</span></Title>
                <IconTop>
                    <ion-icon onClick={() => fazerLogout()} name="log-out-outline"></ion-icon>
                </IconTop>
            </Header>
            {registros.length === 0 ?
                <Container>
                    <Message>
                        Não há registros de <br></br>entrada ou saida
                    </Message>
                </Container> :
                  <Container2>
                  {registros.map(registro => {
                      const { data, descricao, valor, status } = registro;
                      return (
                          <InserirRegistro key={descricao} data={data}
                              descricao={descricao} valor={valor} status={status} />
                      )
                  })}
                  <Saldo>
                      <p>SALDO</p>
                      <Valor saldo={saldo}>{saldo}</Valor>
                  </Saldo>
              </Container2>
            }
            <Footer>
                <SubContainer>
                    <IconFooter onClick={() => navigate("/entrada")}>
                        <ion-icon name="add-circle-outline"></ion-icon>
                    </IconFooter>
                    <p>Nova <br></br>entrada</p>
                </SubContainer>
                <SubContainer>
                    <IconFooter onClick={() => navigate("/saida")}>
                        <ion-icon name="remove-circle-outline"></ion-icon>
                    </IconFooter>
                    <p>Nova <br></br>saida</p>
                </SubContainer>
            </Footer>
        </>
    )
}

function corValor(saldo) {
    if (saldo > 0) return "var(--cor-entrada)"
    else return "var(--cor-saida)"
}

const Valor = styled.span`
    font-size: 17px;
    font-weight: 400;
    color: ${(props) => corValor(props.saldo)};
`
const Header = styled.div`
    display: flex;
    align-itens: center;
`
const Title = styled.h2`
    font-size: 26px;
    font-weight: 700;
    color: #FFFFFF;
    margin-left: 24px;
    margin-top: 25px;
`
const IconTop = styled.button`
    font-size: 23px;
    color: white;
    background-color: var(--cor-fundo);
    border: 1px solid var(--cor-fundo);
    margin-top: 25px;
    margin-left: 160px;
`
const Container = styled.div`
    width: 326px;
    height: 446px;
    border-radius: 5px;
    background-color: #FFFFFF;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-top: 26px;
    margin-left: 25px;
`
const Container2 = styled.div`
    width: 326px;
    min-height: 446px;
    border-radius: 5px;
    background-color: #FFFFFF;
    margin-top: 26px;
    margin-left: 25px;
    padding-bottom: 20px;
    position: relative;
`
const Message = styled.div`
    font-size: 20px;
    color: var(--cor-message);
    text-align: center;
`
const Footer = styled.div`
    display: flex;
    gap: 15px;
    margin-top: 13px;
    margin-bottom: 16px;
    margin-left: 25px;
`
const SubContainer = styled.div`
    width: 155px;
    height: 114px;
    background-color: var(--cor-botao);
        p {
            font-size: 17px;
            font-weight: 700;
            color: #FFFFFF;
            margin-top: 25px;
            margin-left: 10px;
        }
`
const IconFooter = styled.button`
    font-size: 23px;
    font-weight: bold;
    color: white;
    background-color: var(--cor-botao);
    border: 1px solid var(--cor-botao);
    margin-top: 10px;
`
const Saldo = styled.div`
    display: flex;
    justify-content: space-between;
    position: relative;
    margin: 0 10px;
    margin-top: 200px;

        p {
            font-size: 17px;
            font-weight: 700;
            color: var(--cor-descrição);   
        }
`

export default TelaRegistros;
