import { useState, useContext } from "react";
import styled from "styled-components";

function TelaEntrada() {

    const [valorEntrada, setValorEntrada] = useState("");
    const [descriçãoEntrada, setDescriçãoEntrada] = useState("");

    return (
        <Container>
            <Title>Nova Entrada</Title>
            <Inputs>
                <Input type="text" placeholder="E-mail" required
                    onChange={(e) => setValorEntrada(e.target.value)} value={valorEntrada}>
                </Input>
                <Input type="text" placeholder="E-mail" required
                    onChange={(e) => setDescriçãoEntrada(e.target.value)} value={descriçãoEntrada}>
                </Input>
                <Login >Salvar entrada</Login>
            </Inputs>
        </Container>
    )
}

const Container = styled.div`
    width: 375px;
`
const Title = styled.h2`
    font-size: 26px;
    font-weight: 700;
    color: #FFFFFF;
    margin-top: 25px;
    margin-bottom: 40px;
    margin-left: 24px;
`
const Inputs = styled.div`
    display: flex;
    flex-direction: column;
    gap: 13px;
    padding-left: 25px;
`
const Input = styled.input`
        width: 326px;
        height: 58px;
        border-radius: 5px;
        padding-left: 15px;
`
const Login = styled.button`
    font-size: 20px;
    font-weight: 700;
    color: #FFFFFF;
    width: 326px;
    height: 46px;
    border-radius: 5px;
    background-color: var(--cor-botao);
`
export default TelaEntrada;
