import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';

function Menu() {

    const navigate = useNavigate();

    const percentage = 50;

    return (
        <Container>
            <Habitos onClick={() => navigate("/habitos")}>
                Hábitos </Habitos>
            <Hoje onClick={() => navigate("/hoje")}>
                Hoje </Hoje>
            <CircularProgressbar
                value={percentage}
                // text={`${percentage}%`}
                styles={buildStyles({
                    // Rotation of path and trail, in number of turns (0-1)
                    rotation: 0.25,

                    // Whether to use rounded or flat corners on the ends - can use 'butt' or 'round'
                    strokeLinecap: 'butt',

                    // Text size
                    textSize: '16px',

                    // How long animation takes to go from one percentage to another, in seconds
                    pathTransitionDuration: 0.5,

                    // Can specify path transition in more detail, or remove it entirely
                    // pathTransition: 'none',

                    // Colors
                    pathColor: `rgba(82, 182, 255, ${percentage / 100})`,
                    textColor: '#f88',
                    trailColor: '#d6d6d6',
                    backgroundColor: '#FFFFFF',
                })}
            />;            <Historico onClick={() => navigate("/historico")}>
                Histórico </Historico>
        </Container>
    )
}

const Container = styled.div`
    width: 375px;
    height: 70px;
    font-family: 'Lexend Deca', sans-serif;
    font-size: 14px;
    color: var(--cor-azul-claro);
    z-index: 1;
    display: flex;
    justify-content: space-between;
    align-items: center;
    position: fixed;
    bottom: 0;
    left: 0;
    border: 1px solid var(--cor-cinza-borda);
    background-color: #FFFFFF;
`
const Habitos = styled.p`
    margin-left: 36px;
`
const Hoje = styled.p`
`
const Historico = styled.p`
    margin-right: 36px;
`
export default Menu;
