import styled from "styled-components";
import axios from "axios";

function InserirRegistro(props) {

    const tokenLS = localStorage.getItem("token");

    const { data, descricao, valor, status } = props;
    const servidor = "http://localhost:5000/registros";

    const config = {
        headers: {
            "Authorization": `Bearer ${tokenLS}`
        }
    }

    function deletarRegistro() {
        const mensagem = "Deseja deletar o registro?";
        const resultado = window.confirm(mensagem)
        if (resultado) {
            const promise = axios.delete(servidor, config);
            promise.then(response => {
                console.log(response.data);
            })
            promise.catch(err => console.log(err.response.statusText));
        }
    }

    return (
        <UserData>
            <div>
                <Date>{data}</Date>
                <Description>{descricao}</Description>
            </div>
            <Registro status={status}>{valor} <Delete onClick={() => deletarRegistro()}>X</Delete></Registro>
        </UserData>
    )
}

function corRegistro(status) {
    if (status === "entrada") return "var(--cor-entrada)";
    else return "var(--cor-saida)"
}

const UserData = styled.div`
    display: flex;
    justify-content: space-between;
    padding: 0 12px;
    padding-top: 26px;
`
const Date = styled.span`
    color: var(--cor-data);
`
const Description = styled.span`
    color: var(--cor-descrição);
    margin-left: 5px;
`
const Registro = styled.p`
    color: ${(props) => corRegistro(props.status)}
`
const Delete = styled.span`
    color: #C6C6C6;
`
export default InserirRegistro;
