import styled from "styled-components";
import { useState } from "react";

function HabitoHoje(props) {

    // const [iconeSelecionado, setIconeSelecionado] = useState[false]
    const selecionado = true;
    const { habito, atual, recorde, id } = props;
    return (
        <Container>
            <div>
                <h1>{habito}</h1>
                <p>SequÃªncia Atual: {atual} dias</p>
                <p>Seu recorde: {recorde} dias </p>
            </div>
            <Icon selecionado={selecionado}>
                <ion-icon name="checkbox"></ion-icon>
            </Icon>
        </Container>
    )
}

function corIcone (selecionado) {
    if (selecionado) return "green";
    else return "gray";
}

const Container = styled.div`
    width: 340px;
    height: 91px;
    border-radius: 5px;
    border: 1px solid black;
    display: flex;
    justify-content: space-between;
    margin: 10px 0;
    padding-left: 10px;
    background-color: #FFFFFF;

    h1 {
        margin: 10px 0;
    }
`


const Icon = styled.button`
    font-size: 25px;
    background-color: white;
    border: none;
    color: ${(props) => corIcone(props.selecionado)};
`




export default HabitoHoje
