    width: 340px;
    height: 180px;
    border-radius: 5px;
    margin: 0 auto;
    margin-bottom: 28px;
    background-color: #FFFFFF;

        input {
            width: 303px;
            height: 45px;
            border-radius: 5px;
            border: 1px solid var(--cor-cinza-borda);
            margin: 18px 19px;
        }
`
const Days = styled.div`

    display: flex;
    padding-right: 50px;
    background-color: blue;

    button {
        font-size: 19px;
        color: var(--cor-cinza-borda);
        width: 30px;
        height: 30px;
        display: flex;
        justify-content: center;
        align-items: center;
        border-radius: 5px;
        border: 1px solid var(--cor-cinza-borda);
        margin-left: 19px;
        margin-right: 1px;
        background-color: #FFFFFF;
    }
`
const Actions = styled.div `
    display: flex;
    justify-content: right;
    align-items: center;
    padding-top: 29px;
    padding-right: 16px;
    background-color: red;


        p {
            font-size: 16px;
            color: var(--cor-botao-footer);
        }

        button {
            font-size: 16px;
            color: #FFFFFF;
            width: 84px;
            height: 35px;
            display: flex;
            justify-content: center;
            align-items: center;
            border-radius: 5px;
