//     width: 51px;
//     height: 51px;
//     background-image: green;
//     background-size: cover;
//     display: flex;
//     justify-content: center;
//     align-items: center;
// `

const Image = styled.img`
    width: 51px;
    height: 51px;
    margin-right: 18px;
    border-radius: 25px;
`

export default Header;
