import { useState, useContext } from "react";
import UserContext from "../contexts/UserContext";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import Header from "../Layout/Header"

function TelaLogin () {

    const {token, setToken} = useContext(UserContext);

    const [email, setEmail] = useState("");
    const [senha, setSenha] = useState("");

    const API = "https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/auth/login";

    const navigate = useNavigate();


    function fazerLogin() {
        const promise = axios.post(API, {
            email: email,
            password: senha
        });
        promise.then(response => {
            const { data } = response;
            console.log(data.token);
            setToken(data.token);
            navigate("/habitos"); // mudar depois para tela hoje
        }
        )
        promise.catch(response => {
            alert("Informações incorretas, digite novamente email e senha");
        })
    }

    return (
        <>
        <Header />
        <h1>TELA LOGIN</h1>
        <h1>TrackIt</h1>
        <div>
            <input type="text" placeholder="email"
                onChange={(e) => setEmail(e.target.value)}
                value={email}>
            </input>
            <input type="password" placeholder="senha"
                onChange={(e) => setSenha(e.target.value)}
                value={senha}>
            </input>
        </div>
            <Login onClick={fazerLogin}>Entrar</Login>
            {/* <button onClick={fazerLogin}>Entrar</button> */}
            <p onClick={() => navigate("/cadastro")}>Não tem uma conta? Cadastre-se</p>
            </>
    )
}

function corBotao (selecionado) {
    if (selecionado) return "green";
    else return "blue";
}

function resetarBotao (selecionado) {
    if (selecionado) return "none";
    else return "";
}

const Login = styled.button `
font-size: 35px;
color: #FFFFFF;
width: 200px;
height: 150px;
display: flex;
justify-content: center;
align-items: center;
border-radius: 5px;
margin-left: 19px;
pointer-events: ${(props) => resetarBotao(props.selecionado)};
background-color: ${(props) => corBotao(props.selecionado)};
`


export default TelaLogin;
