        <>
            {/* <Topo /> */}

            {visivel ?

                <Container>
                    <Subtitle>
                        <h1>Meus habitos</h1>
                        <button>+</button>
                    </Subtitle>
                    <InserirHabito />
                    <p>Você não tem nenhum hábito cadastrado ainda. Adicione um hábito para começar a trackear!</p>
                </Container>
                :

                <Container>
                    <Subtitle>
                        <h1>Meus habitos</h1>
                        <button onClick={() => setVisivel(true)}>+</button>
                    </Subtitle>
                    {/* <InserirHabito /> */}
                    <p>Você não tem nenhum hábito cadastrado ainda. Adicione um hábito para começar a trackear!</p>
                </Container>

            }
        </>
    )
}

const Container = styled.div`
    background-color: #E5E5E5;
    width: 375px;
    height: 597px;
    margin: 70px 0;

        p {
            font-size: 18px;
            padding-left: 17px;
            padding-right: 20px;
        }
    `
const Subtitle = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding-top: 22px;
    padding-bottom: 28px;


        h1 {
            font-size: 23px;
            color: var(--cor-subtitulo);
            padding-left: 17px;
        }

        button {
            font-size: 27px;
            color: white;
            width: 40px;
            heigth: 35px;
            border-radius: 5px;
            margin-right: 18px;
            background-color: var(--cor-botao-footer)
        }
`

export default TelaHabitos;
