import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';

function Menu() {

    const navigate = useNavigate();

    const percentage = 50;

    return (
        <Container>
            <Habitos onClick={() => navigate("/habitos")}>
                Hábitos </Habitos>
            {/* <Hoje onClick={() => navigate("/hoje")}>
                Hoje </Hoje> */}
            <CircularProgressbar
                className="circularBar"
                value={percentage}
                text={`     ${percentage}%    `}
                styles={buildStyles({
                // Rotation of path and trail, in number of turns (0-1)
                rotation: 0.25,
                textSize: '18px',
                pathTransitionDuration: 0.5,
                pathColor: `white`,
                textColor: 'white',
                trailColor: 'var(--cor-azul-claro)',
                })}
            />           <Historico onClick={() => navigate("/historico")}>
                Histórico </Historico>
        </Container>
    )
}

const Container = styled.div`
    width: 375px;
    height: 70px;
    font-family: 'Lexend Deca', sans-serif;
    font-size: 14px;
    color: var(--cor-azul-claro);
    z-index: 1;
    display: flex;
    justify-content: space-between;
    align-items: center;
    position: fixed;
    bottom: 0;
    left: 0;
    border: 1px solid var(--cor-cinza-borda);
    background-color: #FFFFFF;

    .CircularProgressbar {
        border-radius: 50px;
        padding: 7px;
        margin-bottom: 50px;
        margin-right: 50px;
        margin-left: 50px;
        background-color: var(--cor-azul-claro);
    }
`
const Habitos = styled.p`
    margin-left: 36px;
`
const Hoje = styled.p`
`
const Historico = styled.p`
    margin-right: 36px;
`
export default Menu;
